     1‚Üí/**
     2‚Üí * Neon Database Connection Setup
     3‚Üí * Drizzle ORM with Neon Serverless PostgreSQL
     4‚Üí */
     5‚Üí
     6‚Üíimport { drizzle } from 'drizzle-orm/neon-http';
     7‚Üíimport { neonTables } from './schema';
     8‚Üíimport { log } from '@/lib/logger';
     9‚Üíimport { getNeonConnection, executeQuery } from './connectionPool';
    10‚Üí
    11‚Üí// Lazy initialization to prevent connection attempts in browser
    12‚Üílet sql: ReturnType<typeof getNeonConnection> | null = null;
    13‚Üílet _neonDb: ReturnType<typeof drizzle> | null = null;
    14‚Üí
    15‚Üí// Get SQL connection lazily
    16‚Üífunction getSql() {
    17‚Üí  if (!sql) {
    18‚Üí    sql = getNeonConnection();
    19‚Üí  }
    20‚Üí  return sql;
    21‚Üí}
    22‚Üí
    23‚Üí// Create Drizzle instance lazily
    24‚Üíexport const neonDb = new Proxy({} as ReturnType<typeof drizzle>, {
    25‚Üí  get(target, prop) {
    26‚Üí    if (!_neonDb) {
    27‚Üí      _neonDb = drizzle(getSql(), { schema: neonTables });
    28‚Üí    }
    29‚Üí    return _neonDb[prop as keyof typeof _neonDb];
    30‚Üí  }
    31‚Üí});
    32‚Üí
    33‚Üí// Alias for backward compatibility
    34‚Üíexport const db = neonDb;
    35‚Üí
    36‚Üí// Connection test utility
    37‚Üíexport async function testNeonConnection(): Promise<boolean> {
    38‚Üí  try {
    39‚Üí    await getSql()`SELECT NOW() as current_time`;
    40‚Üí
    41‚Üí    return true;
    42‚Üí  } catch (error) {
    43‚Üí    log.error('Neon connection failed:', { data: error }, 'connection');
    44‚Üí    return false;
    45‚Üí  }
    46‚Üí}
    47‚Üí
    48‚Üí// Database utilities
    49‚Üíexport const neonUtils = {
    50‚Üí  /**
    51‚Üí   * Test database connection
    52‚Üí   */
    53‚Üí  async ping(): Promise<{ success: boolean; timestamp?: string; error?: string }> {
    54‚Üí    try {
    55‚Üí      const result = await executeQuery(sql => sql`SELECT NOW() as timestamp`);
    56‚Üí      const firstRow = Array.isArray(result) && result.length > 0 ? result[0] : null;
    57‚Üí      return {
    58‚Üí        success: true,
    59‚Üí        timestamp: firstRow && typeof firstRow === 'object' && 'timestamp' in firstRow 
    60‚Üí          ? (firstRow as any).timestamp 
    61‚Üí          : new Date().toISOString(),
    62‚Üí      };
    63‚Üí    } catch (error) {
    64‚Üí      return {
    65‚Üí        success: false,
    66‚Üí        error: error instanceof Error ? error.message : 'Unknown error',
    67‚Üí      };
    68‚Üí    }
    69‚Üí  },
    70‚Üí
    71‚Üí  /**
    72‚Üí   * Get database version and info
    73‚Üí   */
    74‚Üí  async getInfo(): Promise<any> {
    75‚Üí    try {
    76‚Üí      const versionResult = await getSql()`SELECT VERSION() as version`;
    77‚Üí      const sizeResult = await getSql()`
    78‚Üí        SELECT 
    79‚Üí          pg_size_pretty(pg_database_size(current_database())) as database_size,
    80‚Üí          current_database() as database_name,
    81‚Üí          current_user as user_name
    82‚Üí      `;
    83‚Üí      
    84‚Üí      const versionRow = Array.isArray(versionResult) && versionResult.length > 0 ? versionResult[0] : null;
    85‚Üí      const sizeRow = Array.isArray(sizeResult) && sizeResult.length > 0 ? sizeResult[0] : null;
    86‚Üí      
    87‚Üí      return {
    88‚Üí        version: versionRow && typeof versionRow === 'object' && 'version' in versionRow
    89‚Üí          ? (versionRow as any).version || 'Unknown'
    90‚Üí          : 'Unknown',
    91‚Üí        ...(sizeRow && typeof sizeRow === 'object' ? sizeRow : {}),
    92‚Üí      };
    93‚Üí    } catch (error) {
    94‚Üí      log.error('Failed to get database info:', { data: error }, 'connection');
    95‚Üí      return null;
    96‚Üí    }
    97‚Üí  },
    98‚Üí
    99‚Üí  /**
   100‚Üí   * Get table information
   101‚Üí   */
   102‚Üí  async getTableStats(): Promise<any> {
   103‚Üí    try {
   104‚Üí      const result = await getSql()`
   105‚Üí        SELECT 
   106‚Üí          schemaname,
   107‚Üí          tablename,
   108‚Üí          n_tup_ins as inserts,
   109‚Üí          n_tup_upd as updates,
   110‚Üí          n_tup_del as deletes,
   111‚Üí          n_live_tup as live_tuples,
   112‚Üí          n_dead_tup as dead_tuples
   113‚Üí        FROM pg_stat_user_tables
   114‚Üí        ORDER BY tablename;
   115‚Üí      `;
   116‚Üí      
   117‚Üí      return result;
   118‚Üí    } catch (error) {
   119‚Üí      log.error('Failed to get table stats:', { data: error }, 'connection');
   120‚Üí      return [];
   121‚Üí    }
   122‚Üí  },
   123‚Üí
   124‚Üí  /**
   125‚Üí   * Execute raw SQL (use with caution)
   126‚Üí   */
   127‚Üí  async rawQuery(query: string): Promise<any> {
   128‚Üí    try {
   129‚Üí      // Use template literal format for Neon
   130‚Üí      return await getSql()([query] as any as TemplateStringsArray);
   131‚Üí    } catch (error) {
   132‚Üí      log.error('Raw query failed:', { data: error }, 'connection');
   133‚Üí      throw error;
   134‚Üí    }
   135‚Üí  },
   136‚Üí};

