     4‚Üí// Conditionally import based on runtime
     5‚Üíconst pino = !isEdgeRuntime ? require('pino') : null;
     6‚Üítype Logger = any;
     7‚Üí
     8‚Üí// Sensitive field patterns to redact
     9‚Üíconst REDACTED_FIELDS = [
    10‚Üí  'password',
    11‚Üí  'token',
    12‚Üí  'secret',
    13‚Üí  'apiKey',
    14‚Üí  'api_key',
