    20‚Üí  'credit_card',
    21‚Üí  'ssn',
    22‚Üí  'socialSecurity',
    23‚Üí  'social_security',
    24‚Üí  'bankAccount',
    25‚Üí  'bank_account',
    26‚Üí  'privateKey',
    27‚Üí  'private_key',
    28‚Üí  'clientSecret',
    29‚Üí  'client_secret',
    30‚Üí  'refreshToken',
    31‚Üí  'refresh_token',
    32‚Üí  'accessToken',
