    37‚Üí
    38‚Üí// Get log level from environment variable
    39‚Üíconst getLogLevel = (): string => {
    40‚Üí  const level = process.env.LOG_LEVEL?.toLowerCase() || 'info';
    41‚Üí  const validLevels = ['fatal', 'error', 'warn', 'info', 'debug', 'trace', 'silent'];
    42‚Üí  return validLevels.includes(level) ? level : 'info';
    43‚Üí};
    44‚Üí
    45‚Üí// Determine if we're in production
    46‚Üíconst isProduction = process.env.NODE_ENV === 'production';
    47‚Üí
    48‚Üí// Create the base logger with simplified config
    49‚Üíconst baseLogger = pino({
    50‚Üí  level: getLogLevel(),
    51‚Üí  
    52‚Üí  // Format for better readability (both dev and prod for now)
    53‚Üí  formatters: {
    54‚Üí    level: (label) => {
    55‚Üí      return { level: label };
    56‚Üí    },
    57‚Üí    bindings: (bindings) => {
    58‚Üí      return { 
    59‚Üí        pid: bindings.pid,
    60‚Üí        host: bindings.hostname,
    61‚Üí        env: process.env.NODE_ENV || 'development'
    62‚Üí      };
    63‚Üí    }
    64‚Üí  },
    65‚Üí  
    66‚Üí  // Redaction configuration
    67‚Üí  redact: {
    68‚Üí    paths: REDACTED_FIELDS.flatMap(field => [
    69‚Üí      field,
    70‚Üí      `*.${field}`,
    71‚Üí      `*.*.${field}`,
    72‚Üí      `req.headers.${field}`,
    73‚Üí      `res.headers.${field}`,
    74‚Üí      `error.${field}`,
    75‚Üí      `err.${field}`
    76‚Üí    ]),
    77‚Üí    censor: '[REDACTED]'
    78‚Üí  },
    79‚Üí  
    80‚Üí  // Timestamp configuration
    81‚Üí  timestamp: pino.stdTimeFunctions.isoTime,
    82‚Üí  
    83‚Üí  // Message key
    84‚Üí  messageKey: 'msg',
    85‚Üí  
    86‚Üí  // Error key
    87‚Üí  errorKey: 'error',
    88‚Üí  
    89‚Üí  // Pretty print in development for better readability
    90‚Üí  ...((!isProduction && process.stdout.isTTY) ? {
    91‚Üí    transport: {
    92‚Üí      target: 'pino-pretty',
    93‚Üí      options: {
    94‚Üí        colorize: true,
    95‚Üí        translateTime: 'HH:MM:ss',
    96‚Üí        ignore: 'pid,hostname',
    97‚Üí        singleLine: false
    98‚Üí      }
    99‚Üí    }
   100‚Üí  } : {})
   101‚Üí});
   102‚Üí
   103‚Üí// Create child loggers for different modules
   104‚Üíexport const createLogger = (module: string): Logger => {
