    49â†’      query: Object.fromEntries(searchParams),
    50â†’      ip: request.ip || request.headers.get('x-forwarded-for'),
    51â†’      userAgent: request.headers.get('user-agent')
    52â†’    });
    53â†’    
    54â†’    // Clone response to log status
    55â†’    const response = NextResponse.next();
    56â†’    
    57â†’    // Log response time
    58â†’    response.headers.set('X-Response-Time', `${Date.now() - startTime}ms`);
    59â†’    
    60â†’    // Log slow requests
